import moment from "moment";
import React, { useContext } from "react";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import styled, { ThemeContext } from "styled-components";
import Button from ".././Components/Button";
import { TweetLocation, Link, Calendar } from "../Assets/Icon";
import { IProfileBoxProps } from "../Helper/interface";
const TweetBox = styled.div`
  display: flex;
  flex-direction: row;
`;
const ProfileContainer = styled.div`
  position: relative;
  font-size: 15px;
  font-weight: lighter;
  line-height: 1.3125;
  flex: 1 1 auto;
`;
const ProfileCover = styled.img`
  background-position: 0 50%;
  background-size: auto auto;
  border-bottom: 1px solid ${(props) => props.theme.color.border};
  border-radius: 2px 2px 0 0;
  @media only screen and (max-width: 700px) {
    width: 100%;
  }
  width: 598px;
  height: 200px;
  object-fit: cover;
  display: block !important;
  z-index: 0;
`;
const ProfileAvatar = styled.img`
  z-index: 1;
  margin-top: -80px;
  margin-left: 30px;
  color: ${(props) => props.theme.color.white};
  height: 134px;
  width: 134px;
  border-radius: 50% !important;
  border: 4px solid ${(props) => props.theme.color.white};
`;
const ProfileButton = styled.div`
  position: absolute;
  right: 20px;
  top: 265px;
`;
const ProfileUser = styled.div`
  margin: 0 0 16px;
  padding: 12px 16px;
`;

const ProfileName = styled.div`
  color: ${(props) => props.theme.color.text};
  font-size: 20px;
  font-weight: 900;
  line-height: 21px;
`;
const ProfileTag = styled.div`
  color: ${(props) => props.theme.color.lightText};
  text-decoration: none;
  margin-bottom: 12px;
`;
const ProfileBio = styled.div`
  color: ${(props) => props.theme.color.text};
  padding: 5px 0 5px 0;
`;
const ProfileInfo = styled.div`
  display: flex;
  flex-direction: row;
  padding: 5px 0 5px 0;
`;
const ProfileStat = styled.div`
  font-size: 14px;
  color: ${(props) => props.theme.color.lightText};
  margin-right: 20px;
  .number {
    font-weight: 900;
    padding-right: 4px;
  }
`;
const Icon = styled.span`
  align-self: center;
  margin-left: auto;
  margin-right: 4px;
  svg {
    width: 20px;
    height: 20px;
    fill: ${(props) => props.theme.color.lightText};
  }
`;
const Text = styled.span`
  color: ${(props) => props.theme.color.lightText};
`;
const ProfileInfoCol = styled(Col)``;

const ProfileBox: React.FC<IProfileBoxProps> = ({ user, authUsername }) => {
  const theme = useContext(ThemeContext);
  function onHandleClick() {
    // Navigate("/settings");
  }
  return (
    <div>
      <TweetBox>
        <div>
          <Row className="ml-0 mr-0 w-100">
            <ProfileCover
              src={user.profile.cover.filename}
              loading="lazy"
              onError={({ currentTarget }) => {
                currentTarget.onerror = null; // prevents looping
                currentTarget.src =
                  "data:image/jpeg;base64,";
              }}
            />
            <ProfileAvatar
              src={user.profile.avatar.filename}
              alt=""
              loading="lazy"
            />
            {user.username === authUsername ? (
              <ProfileButton>
                <Button
                  secondary
                  id="tweets"
                  name="button"
                  type="submit"
                  label="Edit profile"
                  textColor={theme.color.text}
                  hoverBackgroundColor={theme.color.icon}
                  handleClick={onHandleClick}
                />
              </ProfileButton>
            ) : (
              <ProfileButton>
                <Button
                  id="tweets"
                  name="button"
                  type="submit"
                  label="Follow"
                  backgroundColor={theme.color.text}
                  textColor={theme.color.white}
                  hoverBackgroundColor={theme.color.lightText}
                  handleClick={onHandleClick}
                />
              </ProfileButton>
            )}
          </Row>
          <ProfileContainer>
            <ProfileUser>
              <ProfileName>{user.profile.name}</ProfileName>
              <ProfileTag>@{user.username}</ProfileTag>
              <ProfileBio>{user.profile.bio}</ProfileBio>
              <ProfileInfo>
                <Row className="flex-fill">
                  {user.profile.location && (
                    <ProfileInfoCol sm>
                      <Icon>
                        <TweetLocation />
                      </Icon>
                      <Text>{user.profile.location}</Text>
                    </ProfileInfoCol>
                  )}
                  {user.profile.website && (
                    <ProfileInfoCol sm>
                      <Icon>
                        <Link />
                      </Icon>
                      <Text>
                        <a
                          href={user.profile.website}
                          target="_blank"
                          rel="noopener noreferrer"
                        >
                          {user.profile.website}
                        </a>
                      </Text>
                    </ProfileInfoCol>
                  )}
                  <ProfileInfoCol sm>
                    <Icon>
                      <Calendar />
                    </Icon>
                    <Text>
                      {moment
                        .unix(user && user.profile.regDate)
                        .format("MMMM YYYY")}
                    </Text>
                  </ProfileInfoCol>
                </Row>
              </ProfileInfo>
              <ProfileInfo>
                <ProfileStat>
                  <span className="number">{user && user.following}</span>
                  Following
                </ProfileStat>
                <ProfileStat>
                  <span className="number">{user && user.followers}</span>
                  Followers
                </ProfileStat>
              </ProfileInfo>
            </ProfileUser>
            {/* <ProfileButton>
              <Button
                secondary
                id="tweets"
                name="button"
                type="submit"
                label="Tweets"
                onClick={onHandleClick}
              />
              <Button
                secondary
                id="tweetsnreplies"
                name="button"
                type="submit"
                label="Tweets & replies"
              />
              <Button
                secondary
                id="media"
                name="button"
                type="submit"
                label="Media"
              />
              <Button
                secondary
                id="likes"
                name="button"
                type="submit"
                label="Likes"
              />
            </ProfileButton> */}
          </ProfileContainer>
        </div>
      </TweetBox>
    </div>
  );
};
export default ProfileBox;
